mapper.update:
UPDATE meeting_rooms
SET
    room_name = #{roomName},
    is_active = #{isActive}
WHERE room_id = #{roomId}

UPDATE meeting_rooms
SET is_active = 0
WHERE room_id = #{roomId}

mapper.select:
SELECT is_active
FROM meeting_rooms
WHERE room_id = #{roomId}

SELECT COUNT(1)
FROM meeting_rooms
WHERE room_name = #{roomName}

    AND room_id != #{excludeId}

SELECT COUNT(*)
FROM meeting_rooms
WHERE room_id = #{roomId}
